import React from "react";
import { useState, useEffect } from "react";
import axios from "axios";
import UnitInput from "./UnitInput";

function convertUnits(sourceUnit, destinationUnit, value) {
    const sourceRate = sourceUnit.conversionRate;
    const destinationRate = destinationUnit.conversionRate;

    // Calculate the conversion factor
    const conversionFactor = destinationRate / sourceRate;

    // Convert the value using the conversion factor
    const convertedValue = value * conversionFactor;

    return convertedValue;
}

function UnitConverter() {
    const [units, setUnits] = useState([]);
    const [selectedUnit, setSelectedUnit] = useState(null);
    const [conversionRate, setConversionRate] = useState(null);

    useEffect(() => {
        axios.get('http://localhost:8080/api/v1/auth/getAllUnits')
            .then(response => {
                setUnits(response.data);
                console.log(response.data);
            })
            .catch(error => {
                console.log(error);
            });
    }, []);

    const handleUnitChange = event => {
        const selectedUnit = units.find(unit => unit.id === Number(event.target.value));
        setSelectedUnit(selectedUnit);
        setConversionRate(selectedUnit.conversionRate);
    };

    const handleInputChange = event => {
        const input = event.target.value;
        console.log(`Input value: ${input}, Conversion rate: ${conversionRate}`);
        // Perform the conversion here
    };

    return (
        <div>
            <h1>Unit Converter</h1>
            <label htmlFor="unit-selector">Select a unit:</label>
            <select id="unit-selector" onChange={handleUnitChange}>
                <option value="">Select a unit</option>
                {units.map(unit => (
                    <option key={unit.id} value={unit.id}>{unit.unitName}</option>
                ))}
            </select>
            {selectedUnit && (
                <div>
                    <p>Selected unit: {selectedUnit.unitName}</p>
                    <input type="number" onChange={handleInputChange} />
                </div>
            )}
        </div>
    );
}

export default UnitConverter;
